import { FastifyRequest, FastifyReply } from 'fastify';
import { TenantUserRole } from '@akount/db';

/**
 * Role-Based Access Control \(RBAC\) Middleware
 *
 * Enforces role-based permissions for routes.
 * Must be used after authMiddleware and tenantMiddleware.
 *
 * Usage:
 *   preHandler: withRolePermission\(['OWNER', 'ADMIN', 'ACCOUNTANT']\)
 */
export function withRolePermission\(allowedRoles: TenantUserRole[]\) {
  return async \(request: FastifyRequest, reply: FastifyReply\) => {
    if \(!request.tenantRole\) {
      return reply.status\(401\).send\({
        error: 'Unauthorized',
        message: 'Tenant role not found',
      }\);
    }

    if \(!allowedRoles.includes\(request.tenantRole as TenantUserRole\)\) {
      request.log.warn\(
        {
          userId: request.userId,
          tenantId: request.tenantId,
          role: request.tenantRole,
          allowedRoles,
        },
        'Access denied: insufficient permissions'
      \);

      return reply.status\(403\).send\({
        error: 'Forbidden',
        message: 'Insufficient permissions for this operation',
      }\);
    }

    request.log.debug\(
      {
        userId: request.userId,
        tenantId: request.tenantId,
        role: request.tenantRole,
      },
      'RBAC check passed'
    \);
  };
}
EOF)

Bash("apps/api/src/lib/audit.ts" << 'EOF'
import { prisma, AuditAction } from '@akount/db';

/**
 * Audit Logging Service
 *
 * Creates audit trail for financial operations.
 * Required for compliance \(SOC 2, GDPR\) and forensic analysis.
 */
export interface AuditLogParams {
  tenantId: string;
  userId: string;
  entityId?: string;
  model: string;
  recordId: string;
  action: AuditAction;
  before?: Record<string, any>;
  after?: Record<string, any>;
}

export async function createAuditLog\(params: AuditLogParams\): Promise<void> {
  try {
    await prisma.auditLog.create\({
      data: {
        tenantId: params.tenantId,
        userId: params.userId,
        entityId: params.entityId,
        model: params.model,
        recordId: params.recordId,
        action: params.action,
        before: params.before ? JSON.parse\(JSON.stringify\(params.before\)\) : null,
        after: params.after ? JSON.parse\(JSON.stringify\(params.after\)\) : null,
      },
    }\);
  } catch \(error\) {
    // Don't fail the operation if audit logging fails, but log the error
    console.error\('Failed to create audit log:', error\);
  }
}
EOF)
